import React from 'react';
/*"export default" makes this class accesible outside of this file when webpack bundles everything up*/
/*React.Component is the class that comes with React that can be extended to create the components we're working on*/
export default class LikeButton extends React.Component {
    /*where we define what our component will look like or what HTML will be rendered to the application screen*/
    render() {
        /*JSX8
        return <button className="btn btn-primary">Like</button>;
        /*return React.createElement('button', {class: 'btn btn-primary'}, 'Like');*/
    }
}
