import React from 'react';
import ReplyButton from './reply-button';
import LikeButton from './like-button';
import Comment from './comment';

let e = React.createElement

export default class Post extends React.Component {
    constructor(props) {
       super(props);
       this.state = {
        comments: props.comments,
        content: props.content,
        count: 0
       } 
    }
    render() {
        /*map method takes each comments inside this.state.comments and creates a JSX component with the index and comment as the props and pushes it to the comments array*/
        let comments;
        if (this.state.comments) {
            comments = this.state.comments.map((comment, index) => <Comment key={index} {...comment} />)
        }
        return (
            <div className="card w-75">
                <div className="card-header bg-primary text-white">
                    Username and Time
                </div>
                <div className="card-body">
                    {this.state.content}
                </div>
                <div className="card-footer">
                    <LikeButton />
                    <ReplyButton />
                    {comments}
                </div>
            </div>
        )
    }
}
